import React, { useState } from 'react'
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../firebase"
import { async } from '@firebase/util';

const Search = () => {
  const [username, setUsername] = useState("")
  const [user, setUser] = useState(null)
  const [err, setErr] = useState(false)

  const handleSearch = async () => {
    // beware about the database table name:
    const q = query(collection(db, "users"), where("displayName", "==", username));
    try {
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
        setUser(doc.data());
      });
    } catch (err) {
      setErr(true);
    }
  };

  const handleKey = (e) => {
    e.code === "Enter" && handleSearch();
  }

  const handleSelect = (e) =>{
    // check whether the group(chats(userA&userB) in firestore) exists, if not create

  }

  return (
    <div className="search">
      <div className='searchFrom'>
        <input type="text" placeholder='Find a friend' onKeyDown={handleKey} onChange={(e) => setUsername(e.target.value)} />
      </div>
      {err && <span>Sorry, couldnt find user {username}...</span>}
      {user && (
        <div className="userChat" onClick={handleSelect}>
          <img src={user.photoURL} alt="" />
          <div className="userChatInf">
            <span>{user.displayName}</span>
          </div>
        </div>
      )}
    </div>
  )
}

export default Search
