import React, { useState } from 'react'
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../firebase"

const Search = () => {
  const [username, setUsername] = useState("")
  const [user, setUser] = useState(null)
  const [err, setErr] = useState(false)

  const handleSearch = async () => {

    const q = query(collection(db, "user"), where("displayName", "==", username));
    try {
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
        setUser(doc.data)
      });
    } catch (err) {
      setErr(true);
    }
  };

  const handleKey = e => {
    e.code === "Enter" && handleSearch();
  }

  return (
    <div className="search">
      <div className='searchFrom'>
        <input type="text" placeholder='Find a friend' onKeyDown={handleKey} onChange={e => setUsername(e.target.value)} />
      </div>
      {err && <span>Didnt find user {username}</span>}
      {user && <div className="userChat">
        <img src={user.photoURL} alt="" />
        <div className="userChatInf">
          <span>{user.displayName}</span>
        </div>
      </div>}
    </div>
  )
}

export default Search
