import React, { useState } from 'react'
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../firebase"

const Search = () => {
  const [username, setUsername] = useState("")
  const [user, setUser] = useState(null)
  const [err, setErr] = useState(false)

  const handleSearch = async() => {

    const q = query(collection(db, "user"), where("displayName", "==", username));
    const querySnapshot = await getDocs(q);
    querySnapshot.forEach((doc) => {
      // doc.data() is never undefined for query doc snapshots
      console.log(doc.id, " => ", doc.data());
    });
  };

  const handleKey = e => {
    e.code === "Enter" && handleSearch();
  }

  return (
    <div className="search">
      <div className='searchFrom'>
        <input type="text" placeholder='Find a friend' onKeyDown={handleKey} onChange={e => setUsername(e.target.value)} />
      </div>
      <div className="userChat">
        <img src="https://images.pexels.com/photos/39866/entrepreneur-startup-start-up-man-39866.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1" alt="" />
        <div className="userChatInf">
          <span>Yang</span>
        </div>
      </div>
    </div>
  )
}

export default Search
